(function(){ function clearAppContent(){const appElement= document.getElementById('app');
        if (appElement){
            const originalClasses = appElement.className;
            const originalStyles = appElement.getAttribute('style');
            
            appElement.innerHTML = '';
            appElement.innerHTML = `
                <div style="padding: 20px; text-align: center; color: #fff; background: rgba(0,0,0,0.7);">
                <h3>Контент защищен</h3><p>Просмотр исходного кода отключен</p></div>
            `;}}

    function devToolsOpenListener(){clearAppContent();
        window.removeEventListener('devtoolschange', devToolsOpenListener);}
    function setupDevToolsDetection(){
        setInterval(function(){
            const widthThreshold= window.outerWidth -window.innerWidth>160;
            const heightThreshold= window.outerHeight -window.innerHeight>160;
            
            if (!(heightThreshold && widthThreshold) && 
            ((window.Firebug && window.Firebug.chrome && window.Firebug.chrome.isInitialized) || widthThreshold || heightThreshold)) {
                clearAppContent();}},1000);

        window.addEventListener('resize',function(){if(window.outerHeight -window.innerHeight>100 || window.outerWidth -window.innerWidth>100){
                clearAppContent();}});}

    document.addEventListener('contextmenu',function(e){ e.preventDefault();clearAppContent();return false;});
    document.addEventListener('keydown',function(e){
        if (e.ctrlKey && e.keyCode === 85){
            e.preventDefault();
            clearAppContent();
            return false;
        }
        if (e.keyCode === 123) {
            e.preventDefault();
            clearAppContent();
            return false;
        }       
        if (e.ctrlKey && e.shiftKey && e.keyCode === 73){
            e.preventDefault();
            clearAppContent();
            return false;
        }     
        if (e.ctrlKey && e.shiftKey && e.keyCode === 67){
            e.preventDefault();
            clearAppContent();
            return false;
        }
        if (e.ctrlKey && e.shiftKey && e.keyCode === 74){
            e.preventDefault();
            clearAppContent();
            return false;
        }
    });

    window.addEventListener('load',function(){setupDevToolsDetection();
        if (window.outerWidth -window.innerWidth>100 || 
            window.outerHeight -window.innerHeight>100){clearAppContent();}
    });
    Object.defineProperty(document,'visibilityState',{
        get: function(){return 'visible';},
        configurable: false
    });
    Object.defineProperty(document,'hidden',{
        get: function(){return false;},
        configurable: false
    });

    
})();
